package com.chouguting.sicXeLinkingLoaderfun main(args: Array<String>) {    if (args.size < 2) {        println("提供的參數不夠多:")        println("第一個參數要是數字，代表程式的起始位址")        println("之後的參數是你想要連結和載入的檔案名稱")        return    }    //第一個輸入參數要是數字(代表程式的memory位置)    if (!args[0].isHexNumber()) {        println("第一個參數要是數字，代表程式的起始位址")        return    }    val memory = Memory(startAddressHexString = args[0])    val allObjFilesContent = mutableListOf<List<String>>()    for (currentFileIndex in 1 until args.size) {        val fileLoader = FileLoader(args[currentFileIndex])        allObjFilesContent.add(fileLoader.loadFileToLines())    }    LinkingLoader(memory, allObjFilesContent).linkAndLoad()}